import { useState } from "react";
import React from 'react';


const TrickDetected = () => {

    // const [trick, setTrick] = useState(null);
    // const [conf, setConf] = useState(null);

    const [data, setData] = useState({});


    const beginQuery = async() => {
      await fetch("http://127.0.0.1:8000/trick", {
        method: 'GET',
        headers: {
          accept: 'application/json',
        }
      }).then((res => res.json())).then(dataobject => {
        console.log(dataobject);
        setData(dataobject);
      })

    }

    let conf = data["Accuracy"];
    let trick = data["Prediction"];
    let percent = toString(parseInt(conf)*100) + "%";

    return (
        <div id="stats">
          <button
          onClick={beginQuery}>
          Analyze trick
        </button>
          <h2>{trick} detected with {percent} accuracy!</h2>
        </div>
      );


};

export default TrickDetected;
